package com.cisco.operators;

public class BitWiseOperators {

	public static void main(String[] args) {
		int a = 4;
		int b = 3;
		System.out.println("a & b = "+ (a & b));
		System.out.println("a | b = "+ (a | b));
		System.out.println("~a =  "+(a>>2));
		System.out.println("a >> 2 = "+ (a>>2));
		System.out.println("a << 2 = "+ (a<<2));
		
		// TODO Auto-generated method stub

	}

}
